package uet.ktmt.myproject.persistance.entity;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.fasterxml.jackson.datatype.jsr310.deser.LocalDateTimeDeserializer;import com.fasterxml.jackson.datatype.jsr310.ser.LocalDateTimeSerializer;import lombok.*;import org.springframework.data.annotation.CreatedDate;import javax.persistence.*;import java.io.Serializable;import java.time.LocalDateTime;@Entity@NoArgsConstructor@AllArgsConstructor@EqualsAndHashCode@Getter@Setter@Table(name = "history_click")public class HistoryClick implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "history_click_id", nullable = false)    private Long historyClickId;    @CreatedDate    @JsonSerialize(using = LocalDateTimeSerializer.class)    @JsonDeserialize(using = LocalDateTimeDeserializer.class)    @JsonFormat(shape = JsonFormat.Shape.NUMBER)    @Column(name = "click_date")    private LocalDateTime clickDate;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    @ManyToOne    @JoinColumn(name = "product_id")    private Product product;    @Column(name = "clicking")    private Long clicking;}